Scripts.OnClick:
if (this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
GF_WhisperHistoryPriorityListCheckButtonPressed(this:GetID(),getglobal("GF_WhisperHistoryButton"..this:GetID()):GetText(),this:GetChecked())

if (this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end

GF_SavedVariables.usewhoongroups = this:GetChecked();
GF_UpdateResults()
if not GF_SavedVariables.usewhoongroups then
	GF_WhoQueue = {}
	for name,data in GF_SavedVariables.friendsToRemove do if data > time() then GF_SavedVariables.friendsToRemove[name] = nil end end
end

GF_GroupsFrame:Show();
GF_LogFrame:Hide();
GF_SettingsFrame:Hide();
GF_BlackListFrame:Hide();
GF_UpdateBlackListItems();
GF_SavedVariables.mainframelogisopen = nil;

GF_GroupsFrame:Hide();
GF_LogFrame:Show();
GF_SettingsFrame:Hide();
GF_BlackListFrame:Hide();
GF_UpdateBlackListItems();
GF_SavedVariables.mainframelogisopen = true;

Scripts.OnEditFocusLost:
if GF_LFGDescriptionEditBoxHasFocus[2] + .005 < GetTime() then GF_LFGDescriptionEditBoxHasFocus = {nil,GetTime()}; end
GF_PerCharVariables.searchlfgtext = GF_LFGDescriptionEditBox:GetText();
this:HighlightText(string.len(this:GetText())+1);

Scripts.OnClick:
if GF_ResultsListOffset >0 then
	GF_ResultsListOffset = GF_ResultsListOffset - GF_ResultsListOffsetSize;
	GF_UpdateResults();
end

if getn(GF_FilteredResultsList) >GF_ResultsListOffset + GF_ResultsListOffsetSize then
	GF_ResultsListOffset = GF_ResultsListOffset + GF_ResultsListOffsetSize;
	GF_UpdateResults();
end

if GF_BlackListOffset >0 then
	GF_BlackListOffset = GF_BlackListOffset - 20;
	GF_UpdateBlackListItems();
end

if getn(GF_BlackList[GF_RealmName]) >GF_BlackListOffset + 20 then
	GF_BlackListOffset = GF_BlackListOffset + 20;
	GF_UpdateBlackListItems();
end

if (this:GetButtonState() == "PUSHED") then
	this.clickDelay = MESSAGE_SCROLLBUTTON_INITIAL_DELAY;
else
	PlaySound("igChatScrollDown");
	this:GetParent():ScrollDown();
end

if (this:GetButtonState() == "PUSHED") then
	this.clickDelay = MESSAGE_SCROLLBUTTON_INITIAL_DELAY;
else
	PlaySound("igChatScrollUp");
	this:GetParent():ScrollUp();
end

Scripts.OnMouseWheel:
if arg1 == 1 then
	this:ScrollUp();
	this:ScrollUp();
	this:ScrollUp();
else
	this:ScrollDown();
	this:ScrollDown();
	this:ScrollDown();
end

Scripts.OnClick:
GF_SavedVariables.logshowgroup = this:GetChecked();
if GF_SavedVariables.logshowgroup then GF_LogFilters[1] = true GF_LogFilters[2] = true else GF_LogFilters[1] = nil GF_LogFilters[2] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.logshowfiltered = this:GetChecked();
if GF_SavedVariables.logshowfiltered then GF_LogFilters[3] = true else GF_LogFilters[3] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.logshowchat = this:GetChecked();
if GF_SavedVariables.logshowchat then GF_LogFilters[4] = true else GF_LogFilters[4] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.logshowtrades = this:GetChecked();
if GF_SavedVariables.logshowtrades then GF_LogFilters[5] = true else GF_LogFilters[5] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.logshowguilds = this:GetChecked();
if GF_SavedVariables.logshowguilds then GF_LogFilters[11] = true else GF_LogFilters[11] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.logshowloot = this:GetChecked();
if GF_SavedVariables.logshowloot then GF_LogFilters[6] = true else GF_LogFilters[6] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.logshowspam = this:GetChecked();
if GF_SavedVariables.logshowspam then GF_LogFilters[7] = true else GF_LogFilters[7] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.logshowblacklist = this:GetChecked();
if GF_SavedVariables.logshowblacklist then GF_LogFilters[8] = true else GF_LogFilters[8] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.logshowbelowlevel = this:GetChecked();
if GF_SavedVariables.logshowbelowlevel then GF_LogFilters[9] = true else GF_LogFilters[9] = nil end
if GF_WhisperLogCurrentButtonID == 0 then GF_DisplayLog() end

GF_SavedVariables.spamfilter = this:GetChecked();
if not GF_SavedVariables.spamfilter then
	GF_FrameAutoBlacklistCheckButton:SetChecked(false);
	GF_SavedVariables.autoblacklist = false;
end

Scripts.OnValueChanged:
if this:GetValue() > 10 then
	GF_SavedVariables.showgroupsnewonlytime = (this:GetValue()-10)*5 + 10;
	getglobal(this:GetName().."Text"):SetText(GF_NEW_GROUP_TIMEOUT.." - "..GF_SavedVariables.showgroupsnewonlytime..GF_MINUTES);
else
	GF_SavedVariables.showgroupsnewonlytime = this:GetValue();
	getglobal(this:GetName().."Text"):SetText(GF_NEW_GROUP_TIMEOUT.." - "..GF_SavedVariables.showgroupsnewonlytime..GF_MINUTES);
end
