Scripts.OnClick:
if (this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
GF_WhisperHistoryPriorityListCheckButtonPressed(this:GetID(),getglobal("GF_WhisperHistoryButton"..this:GetID()):GetText(),this:GetChecked())

if (this:GetChecked()) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end

if GF_ResultsListOffset >0 then
	GF_ResultsListOffset = GF_ResultsListOffset - 20;
	GF_UpdateResults();
end

if getn(GF_FilteredResultsList) >GF_ResultsListOffset + 20 then
	GF_ResultsListOffset = GF_ResultsListOffset + 20;
	GF_UpdateResults();
end

if GF_BlackListOffset >0 then
	GF_BlackListOffset = GF_BlackListOffset - 20;
	GF_UpdateBlackListItems();
end

if getn(GF_BlackList[GF_RealmName]) >GF_BlackListOffset + 20 then
	GF_BlackListOffset = GF_BlackListOffset + 20;
	GF_UpdateBlackListItems();
end

if (this:GetButtonState() == "PUSHED") then
	this.clickDelay = MESSAGE_SCROLLBUTTON_INITIAL_DELAY;
else
	PlaySound("igChatScrollDown");
	this:GetParent():ScrollDown();
end

if (this:GetButtonState() == "PUSHED") then
	this.clickDelay = MESSAGE_SCROLLBUTTON_INITIAL_DELAY;
else
	PlaySound("igChatScrollUp");
	this:GetParent():ScrollUp();
end

Scripts.OnMouseWheel:
if arg1 == 1 then
	this:ScrollUp();
	this:ScrollUp();
	this:ScrollUp();
else
	this:ScrollDown();
	this:ScrollDown();
	this:ScrollDown();
end

Scripts.OnClick:
GF_SavedVariables.spamfilter = this:GetChecked();
if not GF_SavedVariables.spamfilter then
	GF_FrameAutoBlacklistCheckButton:SetChecked(false);
	GF_SavedVariables.autoblacklist = false;
end
